import { applyMiddleware, combineReducers, createStore } from "redux";
import thunk from "redux-thunk";
import userReducer from "./reducer";

const store = createStore(
  combineReducers({
    user: userReducer
  }),
  {},
  applyMiddleware(thunk)
);

export default store;
